package Q1;import java.util.Scanner;public class prog58b { public static void main (String[ ]args ) {System.out.println("Enter number 1: ");Scanner input = new Scanner(System.in);int a = input.nextInt();System.out.println("Enter number 2: ");int b = input.nextInt();System.out.println("Enter number 3: ");int c = input.nextInt();double root1 = ((-1 * b)+Math.sqrt(Math.pow(b,2.0)-(4*a*c)))/(2*a);double root2 = ((-1 * b)+Math.sqrt(Math.pow(b,2.0)-(4*a*c)))/(2*a);System.out.print("Root 1 is " + root1 + " and root2 is " + root2);}}